import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter);

import Home from '@/pages/home/Home.vue'
import Search from '@/pages/search'
import Login from '@/pages/login/Login.vue'
import Register from '@/pages/register/Register.vue'
import Detail from '@/pages/Detail'

export default new VueRouter({
    routes:[
        {
            path:'/home',
            component:Home,
            meta:{show:true}
        },
        {
            name:'search',
            path:'/search/:keyword?',
            component:Search,
            meta:{show:true}
        },
        {
            path:'/login',   
            component:Login,
            meta:{show:false}
        },
        {
            path:'/register',
            component:Register,
            meta:{show:false}
        },
        {
            path:'/',
            redirect: '/Home'
        },
        {
            path:'/detail',
            component:Detail,
        }

    ]
})
