import Home from '@/pages/Home/Home.vue'
import Search from '@/pages/Search'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
import Login from '@/pages/Login'
import ShopCart from '@/pages/ShopCart'
import Trade from '@/pages/Trade'
import Pay from '@/pages/Pay'
import PaySuccess from '@/pages/PaySuccess'
import Center from '@/pages/Center'

import myOrder from '@/pages/Center/myOrder'
import groupOrder from '@/pages/Center/groupOrder'

export default(
[
    {
        path:'/home',
        component:Home,
        meta:{show:true}
    },
    {
        name:'Search',
        path:'/search/:keyword?',
        component:Search,
        meta:{show:true}
    },
    {
        path:'/login',   
        component:Login,
        meta:{show:false}
    },
    {
        path:'/register',
        component:Register,
        meta:{show:false}
    },
    {
        path:'/',
        redirect: '/home',
    },
    {
        path:'/detail/:skuid?',
        component:Detail,
        meta:{show:true}
    },
    {
        path:'/addCartSuccess',
        name:'AddCartSuccess',
        // lazy loading
        component: () =>import( "@/pages/AddCartSuccess"),
        meta:{Show:true}
    },
    {
        path:'/ShopCart',
        name:'ShopCart',
        component:ShopCart,
        meta:{Show:true}
    },
    {
        path:'/trade',
        name:'Trade',
        component:Trade,
        meta:{Show:true}
    },
    {
        path:'/pay',
        name:'Pay',
        component:Pay,
        meta:{Show:true}
    },
    {
        path:'/paysuccess',
        name:'PaySuccess',
        component:PaySuccess,
        meta:{Show:true}
    },
    {
        path:'/center',
        name:'Center',
        component:Center,
        meta:{Show:true},
        children:[
            {
                path:'myOrder',
                name:'myOrder',
                component:Center,
            },
            {
                path:'groupOrder',
                name:'groupOrder',
                component:groupOrder,
            }
        ]
    },
    
    

]
)
