import{reqCategoryList} from '@/api'

const state ={};
const mutations ={};
const actions = {
    async categoryList({commit}){
        let result = await reqCategoryList()
            if(result.code==200){
                commit('reqCategoryList',result.data)
            }
    }
};
const getters = {

};
export default {
    state,mutations,actions,getters
}
