import Home from '@/pages/home/Home.vue'
import Search from '@/pages/search'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
import Login from '@/pages/Login'
import ShopCart from '@/pages/ShopCart'
export default(
[
    {
        path:'/home',
        component:Home,
        meta:{show:true}
    },
    {
        name:'search',
        path:'/search/:keyword?',
        component:Search,
        meta:{show:true}
    },
    {
        path:'/login',   
        component:Login,
        meta:{show:false}
    },
    {
        path:'/register',
        component:Register,
        meta:{show:false}
    },
    {
        path:'/',
        redirect: '/home',
    },
    {
        path:'/detail/:skuid?',
        component:Detail,
        meta:{show:true}
    },
    {
        path:'/addCartSuccess',
        name:'AddCartSuccess',
        // lazy loading
        component: () =>import( "@/pages/AddCartSuccess"),
        meta:{Show:true}
    },
    {
        path:'/ShopCart',
        name:'ShopCart',
        component:ShopCart,
        meta:{Show:true}
    },
    {
        path:'/trade',
        name:'trade',
        component:Trade,
        meta:{Show:true}
    }

]
)
